import React, { Component } from 'react'
import App from '../apps/militaryTime/App.jsx'
import {useSpring, animated} from 'react-spring'

export default class Basic extends Component {

    constructor(){
        super()

        this.state = {
            ontainerClass: '',
            infoStyle: {}
        }

        this.getContainerClass = this.getContainerClass.bind(this)
        this.getInfoStyle = this.getInfoStyle.bind(this)
    }

    getContainerClass(myClass){
        this.setState({myClass})
    }

    getInfoStyle(infoStyle){
        this.setState({infoStyle})
    }

    render() {
        return (
            <div id="container" class={this.state.myClass}>
                <App giveContainerClass = {this.getContainerClass} giveInfoStyle = {this.getInfoStyle}/>
                <animated.div style = {this.state.infoStyle} id="info">
                    {console.log(this.state.infoStyle)}
                    <ntitle>Military Time</ntitle>
                    <p>You want to convert the time from a 12 hour clock to a 24 hour clock. If you are given the time on a 12 hour clock, you should output the time as it would appear on a 24 hour clock.</p>

                    <p><b>Task:</b>  Determine if the time you are given is AM or PM, then convert that value to the way that it would appear.</p>
                </animated.div>
            </div>
        )
    }
}
