import React, {useState} from 'react'
import {useTransition, useSpring, animated} from 'react-spring'

const Stars = () =>{
    const [starsState] = useState([
        {
            class: 'stars-sm',
            key: '1'
        },
        {
            class: 'stars-m',
            key: '2'
        },
        {
            class: 'stars-l',
            key: '3'
        }
    ])

    const starTransition = useTransition(starsState, star => star.key, {
        from: { opacity: 0 },
        enter: { opacity: 1 },
        leave: { opacity: 0 }
    })

    return <>{starTransition.map(({item, props, key}) => 
        <animated.div key={key} style={props} className={item.class}></animated.div>
        )}</>

}

export default function Bg(props) {

    const bgStyle = useSpring({background: props.bg})

    return (
        <>
            <animated.div style={bgStyle} className="bg"></animated.div>
            {
                (!!props.DN) ? (
                    <div className='sun-sm'></div>
                )
                :
                (
                    <Stars/>
                )
            }
        </>
    )
}
