import React, { Component } from 'react'
import "./style/root.css"
import Moon from './components/Moon.jsx'
import Sun from './components/Sun.jsx'

export default class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            hourI: null,
            minuteI: null,
            periodI: false,
        }

        this.handleHourI = this.handleHourI.bind(this)

        document.body.classList.add('blue');

    }

    handleHourI(event) {
        this.setState({hourI: event.target.value})
    }

    handlePeriodI = () => {
        this.setState({periodI: !this.state.periodI})
    }

    render() {
        return (
            <div id="app-container">
                <div class="s-sm"></div>
                <div class="s-m"></div>
                <div class="s-l"></div>
                <div id="app">
                    <div id="label12" className="hideM">12h system:</div>
                    <div className="hideM"></div>
                    <input id="hourInput" type="text" maxlength="2" placeholder="12" onChange={this.handleHourI} />
                    <div id="dotsInput">:</div>
                    <input id="minuteInput" type="text" maxlength="2" placeholder="12" />
                    <div id="timePeriod" onClick={this.handlePeriodI}>{(this.state.periodI) ? "AM" : "PM"}</div>
                    <div id="bigEmpty"></div>
                    <div id="to" className="hideM">to</div>
                    <div id="label24" className="hideM">24h system:</div>
                    <div className="hideM"></div>
                    <div id="output">
                        <div id="hourOutput">12</div>
                        <div id="dotsOutput">:</div>
                        <div id="minuteOutput">{this.state.minuteI}</div>
                    </div>
                    <div id="shape">
                        {this.state.periodI ? <Sun/> : <Moon/>}
                    </div>
                </div>
            </div>
        )
    }
}
