import React, {useState} from 'react'
import {useTransition, useSpring, animated} from 'react-spring'
import {bg, configA, stars, suns} from './Props.jsx'

export default function Bg(props) {

    const [DN, setDN] = useState(props.DN)
    const [ss, setss] = useState((!!props.DN) ? suns : stars)

    if(DN !== props.DN){
        setss((!!props.DN) ? suns : stars)
        setDN(props.DN)
    }

    const transitions = useTransition(ss, s => s.key, {
        config: configA,
        from: { opacity: 0 },
        enter: s => [{ opacity: s.opacity }],
        leave: { opacity: 0 },
        trail: 20
    })

    return (
        <>
            {console.log(`BG`, `BG-DN ${DN}`, `BG-SS ${ss[0].class}`)}
            <animated.div style={
                useSpring({
                background: bg[DN],
                config: configA})
            } className="bg"></animated.div>
            {transitions.map(({item, props, key}) => <animated.div style={props} key={key} className = {item.class}></animated.div>)}
        </>
    )
}
