import React from 'react'
import {useSpring, animated} from 'react-spring'

export default function Input(props) {

    const inputStyle = useSpring({
        background: props.colors.light,
        color: props.colors.dark,
        placeHolder: props.colors.gray
    })

    return (
        <>
            <div id="label12" className="hideM">12h system:</div>
            <div className="hideM"></div>
            <animated.input style={{...inputStyle, fontSize: 20}} id="hourInput" type="text" maxlength="2" placeholder="12" onChange={props.handleHourI} />
            <div id="dotsInput">:</div>
            <animated.input style={inputStyle} id="minuteInput" type="text" maxlength="2" placeholder="12" onChange={props.handleMinuteI}/>
            <div id="timePeriod" onClick={props.handlePeriodI}>{(props.periodI) ? "AM" : "PM"}</div>
        </>
    )
}
