import React, {useState} from 'react'
import {useTransition, useSpring, animated} from 'react-spring'

export default function Bg(props) {

    const bgStyle = useSpring({background: props.bg})

    const [stars] = useState([
        {
            class: 'stars-sm',
            key: '1'
        },
        {
            class: 'stars-m',
            key: '2'
        },
        {
            class: 'stars-l',
            key: '3'
        }
    ])

    const starTransitions = useTransition(stars, star => star.key, {
        from: { opacity: 0 },
        enter: { opacity: 1 },
        leave: { opacity: 0 }
    });

    return (
        <>
            <animated.div style={bgStyle} className="bg"></animated.div>
            {
                (!!props.DN) ? (
                    <div className='sun-sm'></div>
                )
                :
                (
                    starTransitions.map(({ item, props, key }) => (
                        <animated.div
                          key={key}
                          style={props}
                          className = { 'stars-l' }
                        >
                        </animated.div>
                    ))
                )
            }
        </>
    )
}
