import React, { Component } from 'react'
import "./style/root.css"
import Output from './components/Output.jsx'

export default class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            hourI: null,
            minuteI: null,
            periodI: false,
            hourO: null
        }

        this.handleHourI = this.handleHourI.bind(this)
        this.handleMinuteI = this.handleMinuteI.bind(this)
        this.handlePeriodI = this.handlePeriodI.bind(this)

        document.body.classList.add('red');

    }

    handleHourI(event) {
        this.setState({hourI: event.target.value})
        let hourINT = (parseInt(this.state.hourI))
        console.log(this.state.hourI, hourINT)

        console.log(this.hourO(hourINT))
    }

    handleMinuteI(event) {
        this.setState({minuteI: event.target.value})
    }

    handlePeriodI(){
        this.setState({periodI: !this.state.periodI})
    }

    hourO(hour){
        if(!(hour >= 0 && hour <= 12)) return -1 
        if(!this.state.periodI){
            if(hour !== 12) hour += 12
        }else{
            if(hour === 12) hour = 0
        }
        return hour
    }

    render() {
        return (
            <div id="app-container">
                <div class="s-sm"></div>
                <div class="s-m"></div>
                <div class="s-l"></div>
                <div id="app">
                    <div id="label12" className="hideM">12h system:</div>
                    <div className="hideM"></div>
                    <input id="hourInput" type="text" maxlength="2" placeholder="12" onChange={this.handleHourI} />
                    <div id="dotsInput">:</div>
                    <input id="minuteInput" type="text" maxlength="2" placeholder="12" onChange={this.handleMinuteI}/>
                    <div id="timePeriod" onClick={this.handlePeriodI}>{(this.state.periodI) ? "AM" : "PM"}</div>
                    <div id="bigEmpty"></div>
                    <div id="to" className="hideM">to</div>
                    <Output/>
                </div>
            </div>
        )
    }
}
