import React, { Component } from 'react'
import "./style/root.css"
import Output from './components/Output.jsx'

export default class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            hourI: '',
            minuteI: '',
            periodI: false,
            hourO: -1,
            DN: 0
        }

        this.handleHourI = this.handleHourI.bind(this)
        this.handleMinuteI = this.handleMinuteI.bind(this)
        this.handlePeriodI = this.handlePeriodI.bind(this)
        this.updateHourO = this.updateHourO.bind(this)

        this.props.giveContainerClass('blue')
    }

    updadeContainerClass(DN){
        return (DN) ? 'red' : 'blue'
    }

    handleHourI(event) {
        this.setState({hourI: event.target.value})
    }

    handleMinuteI(event) {
        this.setState({minuteI: event.target.value})
    }

    handlePeriodI(){
        this.setState({periodI: !this.state.periodI})
    }

    componentDidUpdate(_prevProps, prevState){
        if( prevState.hourI !== this.state.hourI || prevState.periodI !== this.state.periodI ){
            let hourO = this.updateHourO()

            this.setState({hourO})
        }
        console.log('componentUpdated')
    }

    updateHourO(){
        let hourI = this.state.hourI
        let periodI = this.state.periodI
        if(hourI === undefined || hourI === '') return -1

        let hour=parseInt(hourI)
        if(hour+'' !== hourI) return -2

        if(!(hour > 0 && hour <= 12)) return -3

        if(!periodI){
            if(hour !== 12) hour += 12
        }else{
            if(hour === 12) hour = 0
        }

        return hour
    }

    render() {
        return (
            <div id="app-container">
                <div class="s-sm"></div>
                <div class="s-m"></div>
                <div class="s-l"></div>
                <div id="app">
                    <div id="label12" className="hideM">12h system:</div>
                    <div className="hideM"></div>
                    <input id="hourInput" type="text" maxlength="2" placeholder="12" onChange={this.handleHourI} />
                    <div id="dotsInput">:</div>
                    <input id="minuteInput" type="text" maxlength="2" placeholder="12" onChange={this.handleMinuteI}/>
                    <div id="timePeriod" onClick={this.handlePeriodI}>{(this.state.periodI) ? "AM" : "PM"}</div>
                    <div id="bigEmpty"></div>
                    <div id="to" className="hideM">to</div>
                    <Output hourO = {this.state.hourO} minuteO = {this.state.minuteO} />
                </div>
            </div>
        )
    }
}
