import React, { Component } from 'react'
import "./style/root.css"
import Output from './components/Output.jsx'
import {useSpring, animated} from 'react-spring'

const AppF = (props) =>{
    const appStyle = useSpring({color: props.colors[props.DN].light})

    return (
        <div id="app-container">
            <div className="bg"></div>
            <div className='stars-sm'></div>
            <div className="stars-m"></div>
            <div className="stars-l"></div>
            <div className="sun-sm"></div>
            <div className="sun-m"></div>
            <div className="sun-l"></div>
            <animated.div style={appStyle} id="app">
                <div id="label12" className="hideM">12h system:</div>
                <div className="hideM"></div>
                <input id="hourInput" type="text" maxlength="2" placeholder="12" onChange={props.handleHourI} />
                <div id="dotsInput">:</div>
                <input id="minuteInput" type="text" maxlength="2" placeholder="12" onChange={props.handleMinuteI}/>
                <div id="timePeriod" onClick={props.handlePeriodI}>{(props.periodI) ? "AM" : "PM"}</div>
                <div id="bigEmpty"></div>
                <div id="to" className="hideM">to</div>
                <Output
                    hourO = { props.hourO }
                    minuteO = { props.minuteO }
                    DN = {props.DN} />
            </animated.div>
        </div>
    )
}

export default class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            hourI: '',
            minuteI: '',
            periodI: false,
            hourO: -1,
            DN: 0,
            colors: [{
                light: '#eff9ff',
                gray: '#8991b2',
                dark: '#232864'
            },
            {
                light: '#fef2e1',
                gray: '#e095a3',
                dark: '#be3865'
            }],
        }

        this.handleHourI = this.handleHourI.bind(this)
        this.handleMinuteI = this.handleMinuteI.bind(this)
        this.handlePeriodI = this.handlePeriodI.bind(this)
        this.updateHourO = this.updateHourO.bind(this)
    }

    handleHourI(event) {
        this.setState({hourI: event.target.value})
    }

    handleMinuteI(event) {
        this.setState({minuteI: event.target.value})
    }

    handlePeriodI(){
        this.setState({periodI: !this.state.periodI})
    }

    componentDidUpdate(_prevProps, prevState){
        if( prevState.hourI !== this.state.hourI || prevState.periodI !== this.state.periodI ){
            let hourO = this.updateHourO(this.state.hourI, this.state.periodI)

            if(hourO > -1){
                let DN = (hourO >= 8 && hourO <= 19) ? 1 : 0

                if(this.state.DN !== DN) 
                    this.setState({DN})
            }

            this.setState({hourO})
        }
    }

    updateHourO(hourI, periodI){
        if(hourI === undefined || hourI === '') return -1

        let hour=parseInt(hourI)
        if(hour+'' !== hourI) return -2

        if(!(hour > 0 && hour <= 12)) return -3

        if(!periodI){
            if(hour !== 12) hour += 12
        }else{
            if(hour === 12) hour = 0
        }

        return hour
    }

    updateMinuteO(minuteI){
        if(minuteI === undefined || minuteI === '') return 12

        let minute = parseInt(minuteI)

        if(minute+'' !== minuteI) return -1

        if(!(minute >= 0 && minute <= 59)) return -1

        return minute
    }

    render() {
        return (
            <AppF
                handlePeriodI = { this.handlePeriodI }
                periodI = { this.state.periodI }
                handleHourI = { this.handleHourI }
                handleMinuteI = { this.handleMinuteI }
                hourO = { this.state.hourO }
                DN = { this.state.DN }
                minuteO = { this.updateMinuteO(this.state.minuteI) }
                colors = { this.state.colors }
            />
        )
    }
}
