import React, { useState } from 'react'
import "./style/root.css"
import Output from './components/Output.jsx'
import Input from './components/Input.jsx'
import Bg from './components/Bg.jsx'
import {useSpring, animated} from 'react-spring'
import {colors, configA, initialDN, infoStyle} from './components/Props.jsx'

export default function App() {
    const [DN, setDN] = useState(initialDN)
    const [hourO, setHourO] = useState(-1)
    const [minuteO, setMinuteO] = useState('')

    const appStyle = useSpring({
        color: colors[DN].light,
        config: configA
    })

    return (
        <div id="app-container">
            {console.log(`APP-DN ${DN}`)}
            {console.log(`App`)}
            <Bg DN = {DN} />

            <animated.div style={appStyle} id="app">

                <Input
                    giveHourO = { setHourO }
                    giveMinuteO = { setMinuteO }
                    giveDN = { setDN }
                />

                <div id="bigEmpty"></div>
                <div id="to" className="hideM">to</div>

                {/*<Output/>*/}

            </animated.div>
        </div>
    )
}


export const initialInfoStyle = infoStyle
