import React from 'react'
import {useSpring, animated} from 'react-spring'

export default function Bg(props) {

    const bgStyle = useSpring({background: props.bg})
    const starStyle = useSpring({opacity: (!props.DN) ? 1 : 0})

    return (
        <>
            <animated.div style={bgStyle} className="bg"></animated.div>
            <animated.div style={starStyle} className='stars-sm'></animated.div>
            <animated.div style={starStyle} className='stars-m'></animated.div>
            <animated.div style={starStyle} className='stars-l'></animated.div>
        </>
    )
}
