import React, { Component } from 'react'

import Moon from './Moon.jsx'
import Sun from './Sun.jsx'

export default class Output extends Component {
    constructor(props){
        super(props)

        this.renderOutput = this.renderOutput.bind(this)
    }

    renderOutput(){
        let hourO = this.props.hourO
        let minuteO = this.props.minuteO

        if(minuteO > -1)
            if(hourO > -1){
                return(
                    <>
                        <div id="hourOutput">{hourO}</div>
                        <div id="dotsOutput">:</div>
                        <div id="minuteOutput">{minuteO}</div>
                    </>
                )
            }
            else if(hourO === -1) return(<div id="textO" class="alfred">Input, sir! ( ˇ෴ˇ )</div>)

        return(<div id="textO" class="alfred">Funny, sir! ( ￣෴￣ )</div>)
    }

    render() {
        return (
            <>
                <div id="label24" className="hideM">24h system:</div>
                <div className="hideM"></div>
                <div id="output">
                    {this.renderOutput()}
                </div>
                <div id="shape">
                    {(this.props.DN) ? <Sun/> : <Moon/>}
                </div>
            </>
        )
    }
}

