import React, {useState} from 'react'
import {useTransition, useSpring, animated} from 'react-spring'

export default function Bg(props) {

    const bgStyle = useSpring({background: props.bg})

    const stars = [
        {
            class: 'stars-sm',
            opacity: 1,
            key: 0
        },
        {
            class: 'stars-m',
            opacity: 1,
            key: 1
        },
        {
            class: 'stars-l',
            opacity: 1,
            key: 2
        }
    ]

    const suns = [
        {
            class: 'sun-sm',
            opacity: 1,
            key: 3
        },
        {
            class: 'sun-m',
            opacity: 0.6,
            key: 4
        },
        {
            class: 'sun-l',
            opacity: 0.3,
            key: 5
        }
    ]

    const [DN, setDN] = useState(props.DN) 
    const [ss, setss] = useState((!!DN) ? suns : stars)

    if(props.DN !== DN){
        setDN(props.DN)
        setss((!!DN) ? suns : stars)
        console.log('am intrat aici')
    }

    console.log(DN)

    const transitions = useTransition(ss, s => s.key, {
        from: { opacity: 0 },
        enter: s => [{ opacity: s.opacity }],
        leave: { opacity: 0 },
    })

    return (
        <>
            <animated.div style={bgStyle} className="bg"></animated.div>
            {transitions.map(({item, props, key}) => console.log(item))}
        </>
    )
}
